mapper.select:
SELECT BASE_CD , BASE_CD_NM , BASE_CD_ABBR , BASE_GROUP_CD , BASE_CD_DESC , ETC1 , ETC2 , ETC3 , ETC4 , ETC5 , ETC6 , ETC7 , ETC8 , ETC9 , ETC10 , USE_YN_CD ,(SELECT BASE_CD_NM FROM TB_SYSTEM_COMMON_CODE B WHERE B.BASE_GROUP_CD='024' AND B.BASE_CD = A.USE_YN_CD) USE_YN_NM , REG_ID , REG_DATE , UPD_ID , UPD_DATE FROM TB_LOCATION_CODE A WHERE BASE_GROUP_CD = #{baseGroupCd}
AND USE_YN_CD = '001'
ORDER BY BASE_CD ASC

