	, BASE_GROUP_CD_DESC
	, USE_YN_CD
	, REG_ID
	, REG_DATE
) 
VALUES (
	#{baseGroupCd}
	, #{baseGroupCdNm}
	, #{baseGroupCdDesc}
	, #{useYnCd}
	, #{regId}
	, getDate() )

mapper.update:

