	TB_PROCESS_GROUP
SET	
	BASE_GROUP_CD = #{baseGroupCd}
	, BASE_GROUP_CD_NM = #{baseGroupCdNm}
	, BASE_GROUP_CD_DESC = #{baseGroupCdDesc}
	, USE_YN_CD = #{useYnCd}
	, UPD_ID = #{updId}
